import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import modal chi tiết sản phẩm
import { useCart } from '../contexts/CartContext'; // Import CartContext
import { useProduct } from '../services/roductService'; // Import ProductService
import '../styles/ProductsPage.css'; // Import file CSS

const ProductsPage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext
  const { products, loading, error } = useProduct(); // Lấy dữ liệu sản phẩm từ ProductService

  // Lấy danh mục từ URL
  const category = new URLSearchParams(window.location.search).get('category');

  // Trạng thái cho modal chi tiết sản phẩm
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Hàm xử lý modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm thêm vào giỏ hàng
  const handleAddToCart = (product) => {
    addToCart(product);
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  // Render sản phẩm
  const renderProducts = (productList) =>
    productList.map((product) => (
      <div className="col-md-4 mb-4" key={product.id}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)}
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  if (loading) return <p>Đang tải sản phẩm...</p>;
  if (error) return <p>Lỗi: {error}</p>;

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">{category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}</h2>
      <div className="container">
        {category ? (
          <div className="row">
            {renderProducts(products.filter((product) => product.category === category))}
          </div>
        ) : (
          [...new Set(products.map((product) => product.category))].map((key) => (
            <div key={key}>
              <h3 className="text-center mb-5">{key.toUpperCase()}</h3>
              <div className="row">
                {renderProducts(products.filter((product) => product.category === key))}
              </div>
            </div>
          ))
        )}
      </div>
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
