import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import hook useNavigate
import axios from 'axios';  // Import axios để gửi request
import "../styles/LoginPage.css";  // Đảm bảo bạn có tệp CSS này cho trang đăng nhập

const LoginPage = () => {
  const [input, setInput] = useState('');  // Lưu trữ giá trị nhập từ người dùng (email hoặc số điện thoại)
  const [password, setPassword] = useState('');  // Lưu trữ mật khẩu
  const [errorMessage, setErrorMessage] = useState('');  // Lưu trữ thông báo lỗi (nếu có)
  const navigate = useNavigate();  // Hook để chuyển hướng trang

  // Hàm xử lý khi người dùng submit form
  const handleSubmit = async (e) => {
    e.preventDefault();  // Ngừng hành động mặc định của form (reload trang)

    // Các biểu thức chính quy dùng để kiểm tra xem input có phải là email hay số điện thoại không
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;

    // Kiểm tra đầu vào, nếu là email hoặc số điện thoại hợp lệ
    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
    } else if (phoneRegex.test(input)) {
      console.log(`Số điện thoại: ${input}, Password: ${password}`);
    } else {
      alert('Vui lòng nhập đúng email hoặc số điện thoại!');
      return;  // Nếu đầu vào không hợp lệ, không chuyển hướng
    }

    // Gửi yêu cầu đăng nhập tới backend
    try {
      const response = await axios.post('http://localhost:5001/api/users/login', {
        emailOrPhone: input,  // Gửi thông tin email hoặc phone
        password: password,  // Mật khẩu
      });

      // Kiểm tra phản hồi từ backend
      if (response.data.success) {
        alert('Đăng nhập thành công!');
        navigate('/');  // Chuyển hướng về trang chủ sau khi đăng nhập thành công
      } else {
        setErrorMessage(response.data.message || 'Có lỗi xảy ra, vui lòng thử lại');
      }
    } catch (error) {
      console.error('Đăng nhập thất bại', error);
      setErrorMessage('Có lỗi xảy ra, vui lòng thử lại');
    }
  };

  return (
    <div className="login-page">  {/* Áp dụng lớp CSS để tạo nền cho trang đăng nhập */}
      <div className="login-container">
        <div className="login-box">
          <h2>LOGIN FORM</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)}  // Cập nhật giá trị input
                required
              />
              <span className="icon">@</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)}  // Cập nhật giá trị mật khẩu
                required
              />
              <span className="icon">🔒</span>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Hiển thị lỗi nếu có */}
            <div className="options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password">Forgot password?</a>
            </div>
            <button type="submit" className="login-btn">LOGIN</button>
          </form>
          <div className="register-link">
            <p>
              Don't have an account? → <a href="/register">Click Here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
