import { createContext, useContext, useState, useEffect } from 'react';

// Tạo Context cho ProductService
const ProductContext = createContext();

// Provider quản lý sản phẩm
export const ProductProvider = ({ children }) => {
  const [categories, setCategories] = useState({}); // Chứa tất cả các danh mục sản phẩm từ API
  const [products, setProducts] = useState([]); // Chứa tất cả sản phẩm từ tất cả danh mục
  const [loading, setLoading] = useState(true);  // Đang tải dữ liệu
  const [error, setError] = useState(null);      // Lỗi khi gọi API

  // Lấy danh sách sản phẩm từ API backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch('http://localhost:5000/api/products'); // URL API sản phẩm
        if (!response.ok) {
          throw new Error('Không thể tải danh sách sản phẩm.');
        }
        const data = await response.json();

        setCategories(data);  // Lưu các danh mục vào trạng thái categories
        // Chuyển tất cả sản phẩm từ các danh mục vào một mảng duy nhất
        const allProducts = Object.values(data).flat(); // Chuyển các danh mục thành 1 mảng lớn
        setProducts(allProducts); // Cập nhật danh sách sản phẩm tổng hợp
      } catch (err) {
        setError(err.message || 'Không thể tải danh sách sản phẩm.'); // Nếu có lỗi, hiển thị thông báo lỗi
      } finally {
        setLoading(false);  // Xong việc, cập nhật trạng thái loading
      }
    };

    fetchProducts();
  }, []);  // useEffect chạy một lần khi component mount

  // Hàm thêm sản phẩm mới
  const addProduct = (newProduct) => {
    setProducts((prevProducts) => [...prevProducts, newProduct]);
  };

  // Hàm xóa sản phẩm theo ID
  const deleteProduct = (productId) => {
    setProducts((prevProducts) =>
      prevProducts.filter((product) => product.id !== productId)
    );
  };

  // Hàm cập nhật sản phẩm theo ID
  const updateProduct = (updatedProduct) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === updatedProduct.id ? updatedProduct : product
      )
    );
  };

  return (
    <ProductContext.Provider
      value={{ products, categories, loading, error, addProduct, deleteProduct, updateProduct }}
    >
      {children}
    </ProductContext.Provider>
  );
};

// Custom hook để sử dụng ProductContext
export const useProduct = () => {
  return useContext(ProductContext);
};
