import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard'; // Import ProductCard component
import ProductModal from '../components/ProductModal'; // Import ProductModal component
import { useCart } from '../contexts/CartContext'; // Import CartContext
import '../styles/HomePage.css';

const HomePage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [selectedProduct, setSelectedProduct] = useState(null); // Trạng thái để mở modal
  const [visibleSections, setVisibleSections] = useState([]); // Lưu các section đã visible

  // Mảng các hình ảnh banner
  const bannerImages = [
    '/assets/images/banner.jpg',
    '/assets/images/banner2.jpg',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % bannerImages.length);
    }, 5000); // Thay đổi hình ảnh mỗi 5 giây

    return () => clearInterval(interval); // Dọn dẹp interval khi component bị unmount
  }, []);

  useEffect(() => {
    // Sử dụng IntersectionObserver để theo dõi khi các section xuất hiện trên màn hình
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const sectionId = entry.target.id;
            setVisibleSections((prevSections) => [...prevSections, sectionId]);
          }
        });
      },
      { threshold: 0.5 } // 50% phần tử phải xuất hiện
    );

    // Theo dõi các section
    const sections = document.querySelectorAll('.product-list');
    sections.forEach((section) => observer.observe(section));

    return () => {
      observer.disconnect();
    };
  }, []);

  const products = [
    { id: 1, image: '/assets/images/pro1.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa tiện dụng và thời trang.' },
    { id: 2, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Chất liệu vải bền, giữ ấm tuyệt vời.' },
    { id: 3, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Dễ dàng mang theo và sử dụng.' },
    { id: 4, image: '/assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', description: 'Áo mưa thông minh, bảo vệ tối đa.' },
    { id: 5, image: '/assets/images/pro5.jpg', name: 'Áo mưa Thông Minh 2', price: '599.000Đ', description: 'Áo mưa chống thấm cực tốt.' },
    { id: 6, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 3', price: '699.000Đ', description: 'Thiết kế thời trang và tiện dụng.' },
    { id: 7, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 1', price: '99.000Đ', description: 'Áo mưa cho bé, dễ dàng sử dụng.' },
    { id: 8, image: '/assets/images/pro8.jpg', name: 'Áo mưa Trẻ Em 2', price: '149.000Đ', description: 'Áo mưa bảo vệ bé khỏi mưa gió.' },
    { id: 9, image: '/assets/images/pro9.jpg', name: 'Áo mưa Trẻ Em 3', price: '199.000Đ', description: 'Áo mưa dễ thương, phù hợp cho trẻ em.' },
  ];

  const handleProductClick = (product) => {
    setSelectedProduct(product); // Mở modal với sản phẩm đã chọn
  };

  const handleCloseModal = () => {
    setSelectedProduct(null); // Đóng modal
  };

  const handleAddToCart = (product) => {
    addToCart(product); // Thêm vào giỏ hàng từ CartContext
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  return (
    <div className="homepage">
      {/* Banner Section */}
      <section className="banner" style={{ backgroundImage: `url(${bannerImages[currentImageIndex]})` }}>
        <div className="banner-content">
          <h2>Áo mưa thời trang</h2>
          <p>Thoải mái và tiện dụng</p>
          <div className="buttons">
            <button className="btn btn-warning me-2">Mua Ngay</button>
            <Link to="/products" className="btn btn-outline-warning">Xem Thêm</Link>
          </div>
        </div>
      </section>

      {/* Áo Mưa Rando */}
      <section className={`product-list ${visibleSections.includes('rando') ? 'visible' : ''}`} id="rando">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Rando</h2>
          <div className="row">
            {products.slice(0, 3).map((product) => (
              <div className="col-md-4 mb-4" key={product.id}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                  onClick={() => handleProductClick(product)} // Mở modal khi click vào sản phẩm
                />
                <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
                  Thêm vào giỏ
                </button>
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Tương tự cho các section khác */}
      {/* Áo Mưa Thông Minh */}
      <section className={`product-list ${visibleSections.includes('thongminh') ? 'visible' : ''}`} id="thongminh">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Thông Minh</h2>
          <div className="row">
            {products.slice(3, 6).map((product) => (
              <div className="col-md-4 mb-4" key={product.id}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                  onClick={() => handleProductClick(product)} // Mở modal khi click vào sản phẩm
                />
                <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
                  Thêm vào giỏ
                </button>
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Áo Mưa Trẻ Em */}
      <section className={`product-list ${visibleSections.includes('treem') ? 'visible' : ''}`} id="treem">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Trẻ Em</h2>
          <div className="row">
            {products.slice(6, 9).map((product) => (
              <div className="col-md-4 mb-4" key={product.id}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                  onClick={() => handleProductClick(product)} // Mở modal khi click vào sản phẩm
                />
                <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
                  Thêm vào giỏ
                </button>
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Modal hiển thị chi tiết sản phẩm */}
      {selectedProduct && (
        <ProductModal product={selectedProduct} onClose={handleCloseModal} />
      )}
    </div>
  );
};

export default HomePage;
