import React from 'react';
import { useCart } from '../contexts/CartContext';
import { useNavigate } from 'react-router-dom';

const Checkout = () => {
  const { cartItems } = useCart();
  const navigate = useNavigate();

  // Tính tổng giá trị giỏ hàng
  const totalPrice = cartItems.reduce(
    (total, item) =>
      total + parseFloat(item.price.replace('Đ', '').replace('.', '').trim()) * item.quantity,
    0
  );

  // Hàm xử lý khi thanh toán
  const handleCheckout = () => {
    // Xử lý thanh toán ở đây (có thể kết nối với API thanh toán hoặc chuyển hướng)
    alert('Thanh toán thành công!');
    navigate('/'); // Quay lại trang chủ sau khi thanh toán
  };

  return (
    <div className="checkout-page">
      <h2 className="text-center mb-5">Thanh Toán</h2>
      <div className="container">
        {/* Giỏ hàng */}
        {cartItems.length > 0 ? (
          <div className="row">
            <div className="col-md-8">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Thông tin giỏ hàng</h5>
                  <ul className="list-group">
                    {cartItems.map((item) => (
                      <li className="list-group-item" key={item.id}>
                        {item.name} x {item.quantity} - {item.price}
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">Tổng tiền</h5>
                  <p className="card-text">Tổng cộng: {totalPrice.toLocaleString()} Đ</p>

                  {/* Form thông tin người dùng */}
                  <h5 className="card-title">Thông tin người nhận</h5>
                  <form>
                    <div className="mb-3">
                      <label htmlFor="name" className="form-label">Họ và tên</label>
                      <input type="text" className="form-control" id="name" required />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="address" className="form-label">Địa chỉ giao hàng</label>
                      <input type="text" className="form-control" id="address" required />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="phone" className="form-label">Số điện thoại</label>
                      <input type="tel" className="form-control" id="phone" required />
                    </div>
                  </form>

                  {/* Nút thanh toán */}
                  <button className="btn btn-success btn-lg mt-3" onClick={handleCheckout}>
                    Xác nhận thanh toán
                  </button>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <p className="text-center">Giỏ hàng của bạn đang trống.</p>
        )}
      </div>
    </div>
  );
};

export default Checkout;
