import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import hook useNavigate
import "../styles/LoginPage.css";

const LoginPage = () => {
  const [input, setInput] = useState(''); // Input chứa email hoặc số điện thoại
  const [password, setPassword] = useState('');
  const navigate = useNavigate();  // Khai báo hook navigate

  const handleSubmit = (e) => {
    e.preventDefault();

    // Kiểm tra đầu vào có phải là email hay số điện thoại
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/; // Số điện thoại có từ 10 đến 15 ký tự

    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
      alert('Đăng nhập thành công với email!');
    } else if (phoneRegex.test(input)) {
      console.log(`Số điện thoại: ${input}, Password: ${password}`);
      alert('Đăng nhập thành công với số điện thoại!');
    } else {
      alert('Vui lòng nhập đúng email hoặc số điện thoại!');
      return;
    }

    // Chuyển hướng người dùng về trang Home sau khi đăng nhập thành công
    navigate('/');  // Chuyển hướng về trang chủ (HomePage)
  };

  return (
    <div className="login-page">
      <div className="container-fluid">
        <div className="login-box">
          <h2>EXISTING LOGIN FORM</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                required
              />
              <span className="icon">@</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <span className="icon">🔒</span>
            </div>
            <div className="options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password">Forgot password?</a>
            </div>
            <button type="submit" className="login-btn">LOGIN</button>
          </form>
          <div className="register-link">
            <p>
              To Register New Account → <a href="/register">Click Here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
