import React, { useState } from "react";
import PropTypes from "prop-types";
import "../styles/ProductModal.css"; // Đường dẫn tới file CSS

const ProductModal = ({ product, onClose }) => {
  const [selectedSize, setSelectedSize] = useState(null);
  const [selectedColor, setSelectedColor] = useState(null);
  const [quantity, setQuantity] = useState(1);

  if (!product) return null; // Không hiển thị nếu không có sản phẩm

  const handleBuyNow = () => {
    if (!selectedSize || !selectedColor) {
      alert("Vui lòng chọn size và màu sắc trước khi mua!");
      return;
    }
    alert(
      `Bạn đã chọn mua sản phẩm: ${product.name}\nSize: ${selectedSize}\nMàu sắc: ${selectedColor}\nSố lượng: ${quantity}`
    );
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Nút đóng modal */}
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i> {/* Icon đóng từ Font Awesome */}
        </button>

        {/* Nội dung modal */}
        <div className="modal-body" style={{ display: "flex", gap: "20px" }}>
          {/* Hình ảnh sản phẩm */}
          <div className="modal-image">
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: "100%",
                height: "auto",
                objectFit: "contain",
                borderRadius: "8px",
              }}
            />
          </div>

          {/* Chi tiết sản phẩm */}
          <div className="modal-details">
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p style={{ fontWeight: "bold", fontSize: "18px", color: "#f8b400" }}>
              Giá: {product.price}
            </p>

            {/* Tùy chọn size */}
            <div>
              <strong>Chọn size:</strong>
              <div className="size-options">
                {["S", "M", "L", "XL"].map((size) => (
                  <div
                    key={size}
                    className={`size-option ${
                      selectedSize === size ? "selected" : ""
                    }`}
                    onClick={() => setSelectedSize(size)}
                  >
                    {size}
                  </div>
                ))}
              </div>
            </div>

            {/* Tùy chọn màu sắc */}
            <div style={{ marginTop: "20px" }}>
              <strong>Chọn màu sắc:</strong>
              <div>
                {["orange", "blue", "green"].map((color) => (
                  <span
                    key={color}
                    className={color}
                    style={{
                      border:
                        selectedColor === color ? "2px solid black" : "1px solid #ccc",
                    }}
                    onClick={() => setSelectedColor(color)}
                  ></span>
                ))}
              </div>
            </div>

            {/* Nhập số lượng */}
            <div style={{ marginTop: "20px" }}>
              <label htmlFor="quantity">
                <strong>Số lượng:</strong>
              </label>
              <input
                type="number"
                id="quantity"
                min="1"
                value={quantity}
                onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                style={{
                  marginLeft: "10px",
                  padding: "5px",
                  width: "60px",
                  border: "1px solid #ccc",
                  borderRadius: "4px",
                }}
              />
            </div>

            {/* Nút Mua ngay */}
            <button className="btn" onClick={handleBuyNow}>
              <i className="fas fa-shopping-cart" style={{ marginRight: "8px" }}></i>
              Mua Ngay
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
  }).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ProductModal;
