import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import modal chi tiết sản phẩm
import { useCart } from '../contexts/CartContext'; // Import CartContext
import '../styles/ProductsPage.css'; // Import file CSS

const ProductsPage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext

  // State to hold products and categories
  const [products, setProducts] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State for the selected product and category
  const [selectedProduct, setSelectedProduct] = useState(null);
  const category = new URLSearchParams(window.location.search).get('category');

  // Fetch products from the backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('/api/products'); // Replace with your actual API endpoint
        const data = await response.json();
        const categorizedProducts = data.reduce((acc, product) => {
          // Assuming each product has a 'category' field
          if (!acc[product.category]) {
            acc[product.category] = [];
          }
          acc[product.category].push(product);
          return acc;
        }, {});
        setProducts(categorizedProducts);
      } catch (err) {
        setError('Error fetching products.');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Hàm xử lý modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm thêm vào giỏ hàng
  const handleAddToCart = (product) => {
    addToCart(product);
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  // Render sản phẩm
  const renderProducts = (productList) =>
    productList.map((product) => (
      <div className="col-md-4 mb-4" key={product.id}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)}
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">{category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}</h2>
      <div className="container">
        {category ? (
          <div className="row">{renderProducts(products[category] || [])}</div>
        ) : (
          Object.keys(products).map((key) => (
            <div key={key}>
              <h3 className="text-center mb-5">{key.toUpperCase()}</h3>
              <div className="row">{renderProducts(products[key])}</div>
            </div>
          ))
        )}
      </div>
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
