import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import ProductModal
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import 'bootstrap/dist/js/bootstrap.bundle.min.js'; // Import Bootstrap JS
import '../styles/HomePage.css'; // Import CSS tùy chỉnh

const HomePage = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    { id: 1, image: '/assets/images/pro1.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa tiện dụng và thời trang.' },
    { id: 2, image: '/assets/images/pro2.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Chất liệu vải bền, giữ ấm tuyệt vời.' },
    { id: 3, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Dễ dàng mang theo và sử dụng.' },
  ];

  // Mở modal với sản phẩm được chọn
  const openModal = (product) => {
    setSelectedProduct(product);
    setModalVisible(true);
  };

  // Đóng modal
  const closeModal = () => {
    setModalVisible(false);
    setSelectedProduct(null);
  };

  return (
    <div className="homepage">
      {/* Banner Section */}
      <section className="banner">
        <div id="bannerCarousel" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            {/* Slide 1 */}
            <div className="carousel-item active">
              <div
                className="banner-slide"
                style={{ backgroundImage: "url('/assets/images/banner.jpg')" }}
              >
                <div className="carousel-caption">
                  <h2>Áo mưa thời trang</h2>
                  <p>Thoải mái và tiện dụng</p>
                </div>
              </div>
            </div>
            {/* Slide 2 */}
            <div className="carousel-item">
              <div
                className="banner-slide"
                style={{ backgroundImage: "url('/assets/images/banner2.jpg')" }}
              >
                <div className="carousel-caption">
                  <h2>Áo mưa bền bỉ</h2>
                  <p>Chất liệu cao cấp, chống thấm tuyệt đối</p>
                </div>
              </div>
            </div>
            {/* Slide 3 */}
            <div className="carousel-item">
              <div
                className="banner-slide"
                style={{ backgroundImage: "url('/assets/images/banner3.jpg')" }}
              >
                <div className="carousel-caption">
                  <h2>Phong cách hiện đại</h2>
                  <p>Vừa tiện lợi, vừa thời trang</p>
                </div>
              </div>
            </div>
          </div>

          {/* Điều khiển Carousel */}
          <button
            className="carousel-control-prev"
            type="button"
            data-bs-target="#bannerCarousel"
            data-bs-slide="prev"
          >
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button
            className="carousel-control-next"
            type="button"
            data-bs-target="#bannerCarousel"
            data-bs-slide="next"
          >
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
      </section>

      {/* Áo Mưa Rando */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Rando</h2>
          <div className="row">
            {products.map((product) => (
              <div className="col-md-4 mb-4" key={product.id} onClick={() => openModal(product)}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                />
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Hiển thị Modal nếu có sản phẩm được chọn */}
      {modalVisible && selectedProduct && (
        <ProductModal product={selectedProduct} onClose={closeModal} />
      )}
    </div>
  );
};

export default HomePage;
