import React, { createContext, useContext, useState, useEffect } from 'react';

// Tạo Context cho giỏ hàng
export const CartContext = createContext();

// Component Provider để quản lý giỏ hàng
export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState(() => {
    // Lấy giỏ hàng từ localStorage nếu có
    const storedCart = localStorage.getItem('cartItems');
    return storedCart ? JSON.parse(storedCart) : [];
  });

  useEffect(() => {
    // Lưu giỏ hàng vào localStorage mỗi khi cartItems thay đổi
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  // Hàm thêm sản phẩm vào giỏ hàng
  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingProduct = prevItems.find((item) => item.id === product.id);
      if (existingProduct) {
        // Cập nhật số lượng nếu sản phẩm đã có
        return prevItems.map((item) =>
          item.id === product.id ? { ...item, quantity: product.quantity } : item
        );
      } else {
        // Nếu sản phẩm chưa có, thêm mới vào giỏ hàng
        return [...prevItems, { ...product, quantity: 1 }];
      }
    });
  };

  // Hàm xóa sản phẩm khỏi giỏ hàng
  const removeFromCart = (productId) => {
    setCartItems((prevItems) => prevItems.filter((item) => item.id !== productId));
  };

  // Hàm lấy tổng số lượng sản phẩm trong giỏ
  const getCartCount = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, getCartCount }}>
      {children}
    </CartContext.Provider>
  );
};

// Custom hook để sử dụng CartContext
export const useCart = () => {
  return useContext(CartContext);
};
