import React from 'react';
import PropTypes from 'prop-types';
import { useCart } from '../contexts/CartContext'; // Để sử dụng hàm addToCart
import '../styles/ProductCard.css';

const ProductCard = ({ id, image, name, price, onClick }) => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext

  const handleAddToCart = () => {
    const product = { id, image, name, price };
    addToCart(product); // Gọi hàm thêm vào giỏ hàng
    alert(`${name} đã được thêm vào giỏ hàng!`); // Thông báo khi thêm vào giỏ hàng
  };

  return (
    <div className="card">
      <img src={image} alt={name} className="card-img-top" />
      <div className="card-body text-center">
        <h5 className="card-title">{name}</h5>
        <p className="card-text">{price}</p>

        {/* Thay nút màu vàng bằng nút xanh (nếu cần giữ nút này) */}
        <button className="btn btn-primary mt-3" onClick={handleAddToCart}>
          Thêm vào giỏ
        </button>
      </div>

      {/* Nút xem chi tiết (vẫn giữ nguyên) */}
      <div className="card-footer text-center">
        <button className="btn btn-info" onClick={onClick}>
          Xem chi tiết
        </button>
      </div>
    </div>
  );
};

ProductCard.propTypes = {
  id: PropTypes.number.isRequired,
  image: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.string.isRequired,
  onClick: PropTypes.func.isRequired, // Đảm bảo có hàm onClick khi click vào sản phẩm
};

export default ProductCard;
