import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar'; // Thanh điều hướng
import Footer from './components/Footer'; // Footer của ứng dụng
import HomePage from './pages/HomePage'; // Trang Home
import ProductsPage from './pages/ProductsPage'; // Trang Sản phẩm
import ProductDetailPage from './pages/ProductDetailPage'; // Trang Chi tiết Sản phẩm
import Cart from './components/Cart'; // Trang Giỏ hàng
import ContactPage from './pages/ContactPage'; // Trang Liên hệ
import ErrorPage from './pages/ErrorPage'; // Trang 404

const App = () => {
  // Giả sử chúng ta quản lý giỏ hàng bằng state
  const [cartItems, setCartItems] = useState([
    { id: 1, image: '/assets/images/pro2.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', quantity: 2 },
    { id: 2, image: '/assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', quantity: 1 },
  ]);

  // Lấy tổng số lượng sản phẩm trong giỏ hàng
  const cartItemsCount = cartItems.reduce((total, item) => total + item.quantity, 0);

  return (
    <Router>
      {/* Navbar hiển thị ở đầu trang và truyền số lượng giỏ hàng */}
      <Navbar cartItemsCount={cartItemsCount} />
      <div className="container mx-auto my-4">
        {/* Định nghĩa các route */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/products" element={<ProductsPage />} />
          <Route path="/products/:id" element={<ProductDetailPage />} /> {/* Route chi tiết sản phẩm */}
          <Route path="/cart" element={<Cart />} />
          <Route path="/contact" element={<ContactPage />} />
          {/* Route bắt tất cả, hiển thị trang lỗi */}
          <Route path="*" element={<ErrorPage />} />
        </Routes>
      </div>
      {/* Footer hiển thị ở cuối trang */}
      <Footer />
    </Router>
  );
};

export default App;
