import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import modal chi tiết sản phẩm
import { useCart } from '../contexts/CartContext'; // Import CartContext
import '../styles/ProductsPage.css'; // Import file CSS

const ProductsPage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext

  // Dữ liệu các sản phẩm
  const products = {
    NhãnHiệuHiếuĐức: [
      { id: 1, image: './assets/images/pro2.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa chất lượng cao với khả năng chống nước tuyệt vời.' },
      { id: 2, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Áo mưa chống thấm nước hiệu quả cho mọi tình huống.' },
      { id: 3, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Áo mưa cao cấp dành cho mọi điều kiện thời tiết.' },
      { id: 4, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 4', price: '399.000Đ', description: 'Chất liệu bền bỉ, thoáng khí và thoải mái.' },
      { id: 5, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 5', price: '399.000Đ', description: 'Thiết kế gọn nhẹ, dễ dàng mang theo trong mọi chuyến đi.' },
      { id: 6, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 6', price: '399.000Đ', description: 'Áo mưa thông minh với khả năng tự động co giãn.' },
    ],
    ÁoMưaThôngMinh: [
      { id: 7, image: '/assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', description: 'Áo mưa thông minh với khả năng phản ứng với thời tiết.' },
      { id: 8, image: '/assets/images/pro5.jpg', name: 'Áo mưa Thông Minh 2', price: '599.000Đ', description: 'Áo mưa với tính năng tự động thay đổi kích thước.' },
      { id: 9, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 3', price: '699.000Đ', description: 'Công nghệ tiên tiến giúp áo mưa luôn khô ráo.' },
      { id: 10, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 4', price: '699.000Đ', description: 'Chất liệu đặc biệt chống thấm nước và thoáng khí.' },
      { id: 11, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 5', price: '699.000Đ', description: 'Cải tiến thiết kế giúp người sử dụng dễ dàng điều chỉnh.' },
      { id: 12, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 6', price: '699.000Đ', description: 'Áo mưa thích hợp cho cả người lớn và trẻ em.' },
    ],
    ÁoMưaTrẻEm: [
      { id: 13, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 1', price: '99.000Đ', description: 'Áo mưa dành cho trẻ em, nhẹ và thoải mái.' },
      { id: 14, image: '/assets/images/pro8.jpg', name: 'Áo mưa Trẻ Em 2', price: '149.000Đ', description: 'Thiết kế dễ dàng mặc cho trẻ em, an toàn và bền bỉ.' },
      { id: 15, image: '/assets/images/pro9.jpg', name: 'Áo mưa Trẻ Em 3', price: '199.000Đ', description: 'Áo mưa chống thấm nước, bảo vệ trẻ khỏi mưa gió.' },
      { id: 16, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 4', price: '99.000Đ', description: 'Áo mưa đáng yêu với nhiều màu sắc tươi sáng.' },
      { id: 17, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 5', price: '99.000Đ', description: 'Sản phẩm chất lượng cao dành cho các bé yêu.' },
      { id: 18, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 6', price: '99.000Đ', description: 'Áo mưa thiết kế an toàn, dễ dàng mặc vào và tháo ra.' },
    ],
  };

  // Lấy danh mục từ URL
  const category = new URLSearchParams(window.location.search).get('category');

  // Trạng thái cho modal chi tiết sản phẩm
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Hàm xử lý modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm thêm vào giỏ hàng
  const handleAddToCart = (product) => {
    addToCart(product);
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  // Render sản phẩm
  const renderProducts = (productList) =>
    productList.map((product) => (
      <div className="col-md-4 mb-4" key={product.id}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)}
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">{category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}</h2>
      <div className="container">
        {category ? (
          <div className="row">{renderProducts(products[category] || [])}</div>
        ) : (
          Object.keys(products).map((key) => (
            <div key={key}>
              <h3 className="text-center mb-5">{key.toUpperCase()}</h3>
              <div className="row">{renderProducts(products[key])}</div>
            </div>
          ))
        )}
      </div>
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
