import React, { useState } from 'react';
import ProductCard from './ProductCard';
import { Link } from 'react-router-dom';

const Cart = () => {
  // Giả lập dữ liệu giỏ hàng
  const [cartItems, setCartItems] = useState([
    { id: 1, image: '../assets/images/pro2.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', quantity: 2 },
    { id: 2, image: '../assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', quantity: 1 },
    { id: 3, image: '../assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 1', price: '99.000Đ', quantity: 3 },
  ]);

  // Tính tổng tiền của giỏ hàng
  const totalPrice = cartItems.reduce((total, item) => total + (parseFloat(item.price.replace('Đ', '').replace('.', '').trim()) * item.quantity), 0);

  // Hàm xử lý thay đổi số lượng sản phẩm trong giỏ hàng
  const handleQuantityChange = (id, newQuantity) => {
    setCartItems(prevItems => prevItems.map(item => 
      item.id === id ? { ...item, quantity: newQuantity } : item
    ));
  };

  // Hàm xử lý xóa sản phẩm khỏi giỏ hàng
  const handleRemoveItem = (id) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== id));
  };

  return (
    <div className="cart-page">
      <h2 className="text-center mb-5">Giỏ Hàng</h2>

      {/* Hiển thị các sản phẩm trong giỏ hàng */}
      <div className="container">
        {cartItems.length > 0 ? (
          <div className="row">
            {cartItems.map((item, index) => (
              <div className="col-md-4 mb-4" key={index}>
                <div className="card">
                  <img src={item.image} alt={item.name} className="card-img-top" />
                  <div className="card-body text-center">
                    <h5 className="card-title">{item.name}</h5>
                    <p className="card-text">Giá: {item.price}</p>
                    <div className="d-flex justify-content-center">
                      <button
                        className="btn btn-secondary me-2"
                        onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                        disabled={item.quantity <= 1}
                      >
                        -
                      </button>
                      <span>{item.quantity}</span>
                      <button
                        className="btn btn-secondary ms-2"
                        onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                      >
                        +
                      </button>
                    </div>
                    <button className="btn btn-danger mt-3" onClick={() => handleRemoveItem(item.id)}>
                      Xóa
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center">Giỏ hàng của bạn đang trống.</p>
        )}
      </div>

      {/* Hiển thị tổng tiền */}
      {cartItems.length > 0 && (
        <div className="container text-center mt-4">
          <h4>Tổng tiền: {totalPrice.toLocaleString()} Đ</h4>
          <Link to="/checkout" className="btn btn-warning mt-3">Tiến hành thanh toán</Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
