import React from 'react';
import PropTypes from 'prop-types';
import { useCart } from '../contexts/CartContext'; // Đã được định nghĩa đúng
import '../styles/ProductCard.css';

const ProductCard = ({ id, image, name, price, oldPrice, discount, onClick }) => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext

  const handleAddToCart = () => {
    const product = { id, image, name, price };
    addToCart(product); // Gọi hàm thêm vào giỏ hàng
  };

  return (
    <div className="card" onClick={onClick}> {/* Thêm onClick tại đây */}
      <img src={image} alt={name} className="card-img-top" />
      <div className="card-body text-center">
        <h5 className="card-title">{name}</h5>
        {oldPrice && <p className="old-price"><del>{oldPrice}</del></p>}
        <p className="card-text">{price}</p>
        {discount && <span className="discount">{discount}</span>}
        <button className="btn btn-warning mt-3" onClick={handleAddToCart}>
          Thêm vào giỏ
        </button>
      </div>
    </div>
  );
};

ProductCard.propTypes = {
  id: PropTypes.number.isRequired,
  image: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.string.isRequired,
  oldPrice: PropTypes.string,
  discount: PropTypes.string,
  onClick: PropTypes.func.isRequired, // Thêm prop onClick
};

export default ProductCard;
