import React, { useState } from 'react';
import "../styles/LoginPage.css";

const LoginPage = () => {
  const [input, setInput] = useState(''); // Input s·∫Ω ch·ª©a email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ki·ªÉm tra ƒë·∫ßu v√†o c√≥ ph·∫£i l√† email hay s·ªë ƒëi·ªán tho·∫°i
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/; // Gi·∫£ s·ª≠ s·ªë ƒëi·ªán tho·∫°i c√≥ t·ª´ 10 ƒë·∫øn 15 k√Ω t·ª±

    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
      alert('ƒêƒÉng nh·∫≠p th√†nh c√¥ng v·ªõi email!');
    } else if (phoneRegex.test(input)) {
      console.log(`S·ªë ƒëi·ªán tho·∫°i: ${input}, Password: ${password}`);
      alert('ƒêƒÉng nh·∫≠p th√†nh c√¥ng v·ªõi s·ªë ƒëi·ªán tho·∫°i!');
    } else {
      alert('Vui l√≤ng nh·∫≠p ƒë√∫ng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i!');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>EXISTING LOGIN FORM</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <input
              type="text"
              placeholder="EMAIL or PHONE"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              required
            />
            <span className="icon">@</span>
          </div>
          <div className="input-group">
            <input
              type="password"
              placeholder="PASSWORD"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <span className="icon">üîí</span>
          </div>
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            <a href="/forgot-password">Forgot password?</a>
          </div>
          <button type="submit" className="login-btn">LOGIN</button>
        </form>
        <div className="register-link">
          <p>
            To Register New Account ‚Üí <a href="/register">Click Here</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
