import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios'; 
import "../styles/LoginPage.css";  

const LoginPage = () => {
  const [input, setInput] = useState('');  
  const [password, setPassword] = useState('');  
  const [errorMessage, setErrorMessage] = useState('');  
  const navigate = useNavigate();  

  const handleSubmit = async (e) => {
    e.preventDefault();  

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;

    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
    } else if (phoneRegex.test(input)) {
      console.log(`Phone: ${input}, Password: ${password}`);
    } else {
      alert('Vui lòng nhập đúng email hoặc số điện thoại!');
      return;  
    }

    try {
      const response = await axios.post('http://localhost:5001/api/users/login', {
        emailOrPhone: input,
        password: password,
      });

      if (response.data.token) {
        localStorage.setItem('token', response.data.token); // Lưu token vào localStorage
        alert('Đăng nhập thành công!');
        navigate('/');  
      } else {
        setErrorMessage(response.data.message || 'Có lỗi xảy ra, vui lòng thử lại');
      }
    } catch (error) {
      console.error('Đăng nhập thất bại', error);
      setErrorMessage('Có lỗi xảy ra, vui lòng thử lại');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-box">
          <h2>LOGIN FORM</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)}  
                required
              />
              <span className="icon">@</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)}  
                required
              />
              <span className="icon">🔒</span>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            <div className="options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password">Forgot password?</a>
            </div>
            <button type="submit" className="login-btn">LOGIN</button>
          </form>
          <div className="register-link">
            <p>
              Don't have an account? → <a href="/register">Click Here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
