import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios';  
import "../styles/RegisterPage.css";  

const RegisterPage = () => {
  const [input, setInput] = useState('');  
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;

    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
    } else if (phoneRegex.test(input)) {
      console.log(`Phone: ${input}, Password: ${password}`);
    } else {
      alert('Vui lòng nhập đúng email hoặc số điện thoại!');
      return;
    }

    // Kiểm tra mật khẩu xác nhận có khớp không
    if (password !== confirmPassword) {
      alert('Mật khẩu xác nhận không khớp!');
      return;
    }

    try {
      // Gửi yêu cầu đăng ký tới backend
      const response = await axios.post('http://localhost:5001/api/users/register', {
        emailOrPhone: input,  // Gửi thông tin email hoặc phone
        password: password,   
      });

      if (response.data.success) {
        alert('Đăng ký thành công!');
        navigate('/login');  // Chuyển hướng về trang đăng nhập
      } else {
        setErrorMessage(response.data.message || 'Có lỗi xảy ra, vui lòng thử lại');
      }
    } catch (error) {
      console.error('Đăng ký thất bại', error);
      setErrorMessage('Có lỗi xảy ra, vui lòng thử lại');
    }
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-box">
          <h2>REGISTER FORM</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                required
              />
              <span className="icon">@</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <span className="icon">🔒</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="CONFIRM PASSWORD"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <span className="icon">🔒</span>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            <button type="submit" className="register-btn">REGISTER</button>
          </form>
          <div className="login-link">
            <p>Already have an account? → <a href="/login">Login Here</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
