import React from 'react';
import { useCart } from '../contexts/CartContext'; // Context giỏ hàng

const Homepage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext

  // Dữ liệu sản phẩm tiêu biểu
  const products = [
    { id: 1, image: './assets/images/pro2.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa chất lượng cao với khả năng chống nước tuyệt vời.' },
    { id: 2, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Áo mưa chống thấm nước hiệu quả cho mọi tình huống.' },
    { id: 3, image: './assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Áo mưa cao cấp dành cho mọi điều kiện thời tiết.' },
    { id: 4, image: './assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', description: 'Áo mưa thông minh với khả năng phản ứng với thời tiết.' },
  ];

  // Hàm thêm vào giỏ
  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div className="homepage">
      {/* Banner */}
      <div className="banner">
        <h2>Chào mừng bạn đến với cửa hàng</h2>
        <p>Khám phá các sản phẩm mới nhất của chúng tôi</p>
        <div className="buttons">
          <button className="btn btn-primary">Xem Sản Phẩm</button>
        </div>
      </div>

      {/* Product List */}
      <div className="product-list">
        <h2>Danh Mục Sản Phẩm</h2>
        <div className="row">
          {products.map((product) => (
            <div key={product.id} className="col-md-4 mb-4">
              <div className="card">
                <img src={product.image} alt={product.name} />
                <div className="card-body">
                  <h5>{product.name}</h5>
                  <p>{product.description}</p>
                  <p><strong>{product.price}</strong></p>
                  <button
                    className="btn btn-warning"
                    onClick={() => handleAddToCart(product)}
                  >
                    Thêm vào giỏ
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
