import React from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css'; // Đảm bảo đã import Bootstrap CSS

const ProductModal = ({ product, onClose }) => {
  if (!product) return null; // Nếu không có sản phẩm thì không hiển thị modal

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Nút đóng modal */}
        <button className="btn-close" onClick={onClose}></button>

        <div className="modal-body d-flex">
          {/* Hình ảnh sản phẩm */}
          <div className="modal-image" style={{ width: '40%' }}>
            <img src={product.image} alt={product.name} className="img-fluid" />
          </div>

          {/* Thông tin sản phẩm */}
          <div className="modal-details" style={{ width: '60%', paddingLeft: '20px' }}>
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p><strong>{product.price}</strong></p>

            {/* Hai nút Mua Ngay và Thêm vào giỏ hàng */}
            <div className="button-container">
              <button className="btn btn-warning me-2">Mua Ngay</button>
              <button className="btn btn-secondary">Thêm vào giỏ hàng</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.object, // Sản phẩm chi tiết
  onClose: PropTypes.func.isRequired, // Hàm đóng modal
};

export default ProductModal;
