import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import modal chi tiết sản phẩm
import { useCart } from '../contexts/CartContext'; // Import CartContext để quản lý giỏ hàng
import { useProduct } from '../services/productService'; // Import ProductService để lấy sản phẩm từ API
import { toast } from 'react-toastify'; // Thêm thư viện thông báo
import '../styles/ProductsPage.css'; // Import file CSS cho trang sản phẩm

const ProductsPage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext
  const { products, loading, error } = useProduct(); // Sử dụng ProductService để lấy dữ liệu

  const category = new URLSearchParams(window.location.search).get('category'); // Lấy danh mục từ URL

  const [selectedProduct, setSelectedProduct] = useState(null); // Trạng thái cho modal chi tiết sản phẩm

  // Hàm xử lý khi nhấn vào sản phẩm để mở modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm xử lý thêm sản phẩm vào giỏ hàng
  const handleAddToCart = (product) => {
    if (!product.id) {
      toast.error("Sản phẩm không hợp lệ!"); // Sử dụng thông báo error từ react-toastify
      return;
    }

    addToCart(product); // Thêm sản phẩm vào giỏ hàng
    toast.success(`${product.name} đã được thêm vào giỏ hàng!`); // Thông báo thành công
  };

  // Hàm render danh sách sản phẩm
  const renderProducts = (productList) => 
    productList.map((product, index) => (
      <div className="col-md-4 mb-4" key={index}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)}
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  // Nếu đang tải dữ liệu hoặc có lỗi
  if (loading) return <div>Đang tải dữ liệu...</div>;
  if (error) return <div>Đã có lỗi xảy ra: {error}</div>;

  // Kiểm tra nếu có sản phẩm hoặc không có sản phẩm trong category
  const renderCategoryProducts = (category) => {
    const categoryProducts = products[category] || [];

    if (categoryProducts.length === 0) {
      return <div>Không có sản phẩm trong danh mục này.</div>;
    }

    return renderProducts(categoryProducts);
  };

  const renderAllProducts = () => {
    return Object.keys(products).map((category) => (
      <div key={category}>
        <h3 className="text-center mb-5">{category.toUpperCase()}</h3>
        <div className="row">{renderCategoryProducts(category)}</div>
      </div>
    ));
  };

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">
        {category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}
      </h2>
      <div className="container">
        {category ? renderCategoryProducts(category) : renderAllProducts()}
      </div>

      {/* Hiển thị modal chi tiết sản phẩm khi có sản phẩm được chọn */}
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
