import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import modal chi tiết sản phẩm
import { useCart } from '../contexts/CartContext'; // Import CartContext
import { useProduct } from '../services/productService'; // Import ProductService
import '../styles/ProductsPage.css'; // Import file CSS

const ProductsPage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext
  const { products, categories, loading, error } = useProduct(); // Sử dụng ProductService

  // Lấy danh mục từ URL
  const category = new URLSearchParams(window.location.search).get('category');

  // Trạng thái cho modal chi tiết sản phẩm
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Hàm xử lý modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm thêm vào giỏ hàng
  const handleAddToCart = (product) => {
    // Kiểm tra xem sản phẩm có id không trước khi thêm vào giỏ hàng
    if (!product.id) {
      alert("Sản phẩm không hợp lệ!");
      return;
    }
    
    // Gọi hàm addToCart từ CartContext để thêm sản phẩm vào giỏ hàng
    addToCart(product);

    // Hiển thị thông báo cho người dùng
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  // Render sản phẩm
  const renderProducts = (productList) =>
    productList.map((product, index) => (
      <div className="col-md-4 mb-4" key={index}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)}
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  // Nếu đang tải dữ liệu hoặc có lỗi
  if (loading) return <div>Đang tải dữ liệu...</div>;
  if (error) return <div>Đã có lỗi xảy ra: {error}</div>;

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">
        {category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}
      </h2>
      <div className="container">
        {category ? (
          <div className="row">{renderProducts(products[category] || [])}</div>
        ) : (
          Object.keys(products).map((key) => (
            <div key={key}>
              <h3 className="text-center mb-5">{key.toUpperCase()}</h3>
              <div className="row">{renderProducts(products[key])}</div>
            </div>
          ))
        )}
      </div>
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
