import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../api/userAPI'; // Import h√†m login t·ª´ userAPI
import "../styles/LoginPage.css"; // Import CSS

const LoginPage = () => {
  const [input, setInput] = useState(''); // Tr∆∞·ªùng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i
  const [password, setPassword] = useState(''); // Tr∆∞·ªùng m·∫≠t kh·∫©u
  const [errorMessage, setErrorMessage] = useState(''); // Th√¥ng b√°o l·ªói
  const navigate = useNavigate(); // ƒêi·ªÅu h∆∞·ªõng trang

  const handleSubmit = async (e) => {
    e.preventDefault(); // NgƒÉn ch·∫∑n h√†nh vi m·∫∑c ƒë·ªãnh c·ªßa form

    // Ki·ªÉm tra input h·ª£p l·ªá
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;
    if (!emailRegex.test(input) && !phoneRegex.test(input)) {
      alert('Vui l√≤ng nh·∫≠p ƒë√∫ng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i!');
      return;
    }

    try {
      const response = await login(input, password); // G·ªçi API ƒëƒÉng nh·∫≠p

      // N·∫øu c√≥ token, l∆∞u v√†o localStorage v√† chuy·ªÉn h∆∞·ªõng
      if (response.data.token) {
        localStorage.setItem('token', response.data.token); // L∆∞u token v√†o localStorage
        alert('ƒêƒÉng nh·∫≠p th√†nh c√¥ng!');
        navigate('/'); // Chuy·ªÉn h∆∞·ªõng v·ªÅ trang ch·ªß
      }
    } catch (error) {
      console.error('L·ªói ƒëƒÉng nh·∫≠p:', error);
      setErrorMessage('ƒêƒÉng nh·∫≠p th·∫•t b·∫°i. Vui l√≤ng ki·ªÉm tra th√¥ng tin!');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-box">
          <h2>LOGIN FORM</h2>
          <form onSubmit={handleSubmit}>
            {/* Tr∆∞·ªùng nh·∫≠p email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i */}
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)} // C·∫≠p nh·∫≠t gi√° tr·ªã input
                required
              />
              <span className="icon">@</span>
            </div>
            {/* Tr∆∞·ªùng nh·∫≠p m·∫≠t kh·∫©u */}
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)} // C·∫≠p nh·∫≠t m·∫≠t kh·∫©u
                required
              />
              <span className="icon">üîí</span>
            </div>
            {/* Hi·ªÉn th·ªã l·ªói n·∫øu c√≥ */}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {/* C√°c t√πy ch·ªçn */}
            <div className="options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password">Forgot password?</a>
            </div>
            {/* N√∫t ƒëƒÉng nh·∫≠p */}
            <button type="submit" className="login-btn">LOGIN</button>
          </form>
          {/* Li√™n k·∫øt ƒë·∫øn trang ƒëƒÉng k√Ω */}
          <div className="register-link">
            <p>
              Don't have an account? ‚Üí <a href="/register">Click Here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
