import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import modal chi tiết sản phẩm
import { useCart } from '../contexts/CartContext'; // Import CartContext
import '../styles/ProductsPage.css'; // Import file CSS

const ProductsPage = () => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext
  const [products, setProducts] = useState([]); // Dữ liệu sản phẩm từ API
  const [loading, setLoading] = useState(true); // Trạng thái loading
  const [error, setError] = useState(null); // Trạng thái lỗi
  const [selectedProduct, setSelectedProduct] = useState(null); // Sản phẩm đang được chọn để hiển thị chi tiết

  // Lấy danh mục từ URL
  const category = new URLSearchParams(window.location.search).get('category');

  // Lấy danh sách sản phẩm từ API backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch('http://localhost:5000/api/products'); // Thay đổi URL nếu cần
        const data = await response.json();

        // Chuyển tất cả sản phẩm từ các danh mục thành một mảng duy nhất
        const allProducts = Object.values(data).flat();
        setProducts(allProducts); // Cập nhật trạng thái sản phẩm
      } catch (err) {
        setError('Không thể tải danh sách sản phẩm.');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Hàm xử lý modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm thêm vào giỏ hàng
  const handleAddToCart = (product) => {
    addToCart(product);
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  // Render sản phẩm theo danh mục
  const renderProducts = (productList) =>
    productList.map((product) => (
      <div className="col-md-4 mb-4" key={product.id}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)}
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  // Hiển thị trang sản phẩm
  if (loading) {
    return <div>Đang tải sản phẩm...</div>; // Hiển thị khi đang tải
  }

  if (error) {
    return <div>{error}</div>; // Hiển thị khi có lỗi
  }

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">{category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}</h2>
      <div className="container">
        {category ? (
          <div className="row">
            {renderProducts(products.filter(product => product.category === category))}
          </div>
        ) : (
          Object.keys(products).map((key) => (
            <div key={key}>
              <h3 className="text-center mb-5">{key.toUpperCase()}</h3>
              <div className="row">{renderProducts(products)}</div>
            </div>
          ))
        )}
      </div>
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
