import React from 'react';
import PropTypes from 'prop-types';
import { useCart } from '../contexts/CartContext'; // Import CartContext để sử dụng hàm addToCart
import '../styles/ProductModal.css'; // Đảm bảo bạn có CSS cho modal

const ProductModal = ({ product, onClose }) => {
  const { addToCart } = useCart(); // Lấy hàm addToCart từ CartContext

  if (!product) return null; // Nếu không có sản phẩm thì không hiển thị modal

  // Hàm thêm sản phẩm vào giỏ hàng
  const handleAddToCart = () => {
    addToCart(product); // Thêm sản phẩm vào giỏ hàng
    alert(`${product.name} đã được thêm vào giỏ hàng!`); // Thông báo khi thêm vào giỏ hàng
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Nút đóng modal */}
        <button className="modal-close" onClick={onClose}>X</button>

        <div className="modal-body" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          {/* Phần hình ảnh */}
          <div className="modal-image" style={{ width: '40%', height: 'auto' }}>
            <img src={product.image} alt={product.name} className="modal-image" style={{ width: '100%', height: 'auto', objectFit: 'contain' }} />
          </div>

          {/* Phần thông tin */}
          <div className="modal-details" style={{ width: '50%', paddingLeft: '30px' }}>
            <h2>{product.name}</h2>
            <p>{product.description}</p> {/* Nếu có mô tả sản phẩm */}
            <p><strong>{product.price}</strong></p>
            <div className="button-container">
              <button className="btn btn-warning">Mua Ngay</button>
              <button className="btn btn-secondary" onClick={handleAddToCart}>Thêm vào giỏ hàng</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired, // Kiểm tra id sản phẩm
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
  }), // Kiểm tra dữ liệu product
  onClose: PropTypes.func.isRequired, // Hàm đóng modal
};

export default ProductModal;
