import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '../styles/ProductModal.css'; // Đường dẫn CSS modal

const ProductModal = ({ product, onClose }) => {
  if (!product) return null; // Không hiển thị nếu không có sản phẩm

  const [size, setSize] = useState('S');
  const [color, setColor] = useState('orange');
  const [quantity, setQuantity] = useState(1);

  const handleBuyNow = () => {
    alert(`Bạn đã chọn mua ngay sản phẩm: ${product.name} - Size: ${size}, Màu: ${color}, Số lượng: ${quantity}`);
    // Có thể thêm logic chuyển hướng đến trang thanh toán tại đây
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Nút đóng */}
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i> {/* Icon đóng */}
        </button>

        {/* Nội dung chi tiết sản phẩm */}
        <div className="modal-body" style={{ display: 'flex', alignItems: 'flex-start', gap: '20px' }}>
          {/* Hình ảnh sản phẩm */}
          <div className="modal-image" style={{ flex: '1' }}>
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: '100%',
                height: 'auto',
                objectFit: 'contain',
                borderRadius: '8px',
              }}
            />
          </div>

          {/* Chi tiết sản phẩm */}
          <div className="modal-details" style={{ flex: '2' }}>
            <h2 style={{ marginBottom: '15px' }}>{product.name}</h2>
            <p style={{ fontSize: '14px', color: '#666', marginBottom: '20px' }}>{product.description}</p>
            <p style={{ fontWeight: 'bold', fontSize: '18px', color: '#ff5722' }}>
              Giá hiện tại: {product.price}
            </p>

            {/* Tùy chọn size và màu sắc */}
            <div style={{ margin: '20px 0' }}>
              <div>
                <strong>Size:</strong>
                <div>
                  <span
                    style={{
                      marginRight: '10px',
                      cursor: 'pointer',
                      fontWeight: size === 'S' ? 'bold' : 'normal',
                    }}
                    onClick={() => setSize('S')}
                  >
                    S
                  </span>
                  <span
                    style={{
                      marginRight: '10px',
                      cursor: 'pointer',
                      fontWeight: size === 'M' ? 'bold' : 'normal',
                    }}
                    onClick={() => setSize('M')}
                  >
                    M
                  </span>
                  <span
                    style={{
                      marginRight: '10px',
                      cursor: 'pointer',
                      fontWeight: size === 'L' ? 'bold' : 'normal',
                    }}
                    onClick={() => setSize('L')}
                  >
                    L
                  </span>
                  <span
                    style={{
                      marginRight: '10px',
                      cursor: 'pointer',
                      fontWeight: size === 'XL' ? 'bold' : 'normal',
                    }}
                    onClick={() => setSize('XL')}
                  >
                    XL
                  </span>
                </div>
              </div>

              <div style={{ marginTop: '10px' }}>
                <strong>Màu sắc:</strong>
                <span style={{ marginLeft: '10px', display: 'flex', gap: '10px' }}>
                  <span
                    style={{
                      width: '20px',
                      height: '20px',
                      backgroundColor: 'orange',
                      borderRadius: '50%',
                      border: '1px solid #ccc',
                      display: 'inline-block',
                      cursor: 'pointer',
                      borderWidth: color === 'orange' ? '3px' : '1px',
                    }}
                    onClick={() => setColor('orange')}
                  />
                  <span
                    style={{
                      width: '20px',
                      height: '20px',
                      backgroundColor: 'blue',
                      borderRadius: '50%',
                      border: '1px solid #ccc',
                      display: 'inline-block',
                      cursor: 'pointer',
                      borderWidth: color === 'blue' ? '3px' : '1px',
                    }}
                    onClick={() => setColor('blue')}
                  />
                  <span
                    style={{
                      width: '20px',
                      height: '20px',
                      backgroundColor: 'green',
                      borderRadius: '50%',
                      border: '1px solid #ccc',
                      display: 'inline-block',
                      cursor: 'pointer',
                      borderWidth: color === 'green' ? '3px' : '1px',
                    }}
                    onClick={() => setColor('green')}
                  />
                </span>
              </div>
            </div>

            {/* Nhập số lượng */}
            <div style={{ marginBottom: '20px' }}>
              <label htmlFor="quantity" style={{ fontWeight: 'bold' }}>Số lượng đặt mua:</label>
              <input
                type="number"
                id="quantity"
                min="1"
                defaultValue="1"
                style={{
                  marginLeft: '10px',
                  padding: '5px',
                  width: '60px',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                }}
                onChange={(e) => setQuantity(e.target.value)}
              />
            </div>

            {/* Nút Mua ngay */}
            <button
              className="btn btn-warning"
              style={{
                padding: '10px 20px',
                fontSize: '16px',
                backgroundColor: '#ff9800',
                border: 'none',
                color: '#fff',
                borderRadius: '4px',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
              }}
              onClick={handleBuyNow}
            >
              <i className="fas fa-shopping-cart" style={{ marginRight: '8px' }}></i> {/* Icon giỏ hàng */}
              Mua Ngay
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
  }).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ProductModal;
