import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard'; // Import ProductCard component
import '../styles/HomePage.css';

const HomePage = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const bannerImages = [
    '/assets/images/banner1.jpg',
    '/assets/images/banner2.jpg',
    '/assets/images/banner3.jpg',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % bannerImages.length);
    }, 5000); // Thay đổi hình ảnh mỗi 5 giây

    return () => clearInterval(interval); // Dọn dẹp interval khi component bị unmount
  }, []);

  const products = [
    { id: 1, image: '/assets/images/pro1.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa tiện dụng và thời trang.' },
    { id: 2, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Chất liệu vải bền, giữ ấm tuyệt vời.' },
    { id: 3, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Dễ dàng mang theo và sử dụng.' },
  ];

  return (
    <div className="homepage">
      {/* Banner Section */}
      <section className="banner" style={{ backgroundImage: `url(${bannerImages[currentImageIndex]})` }}>
        <div className="banner-content">
          <h2>Áo mưa thời trang</h2>
          <p>Thoải mái và tiện dụng</p>
          <div className="buttons">
            <button className="btn btn-warning me-2">Mua Ngay</button>
            <button className="btn btn-outline-warning">Xem Thêm</button>
          </div>
        </div>
      </section>

      {/* Áo Mưa Rando */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Rando</h2>
          <div className="row">
            {products.map((product) => (
              <div className="col-md-4 mb-4" key={product.id}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                />
              </div>
            ))}
          </div>
          {/* Nút xem thêm sản phẩm */}
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Áo Mưa Thông Minh */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Thông Minh</h2>
          <div className="row">
            <div className="col-md-4 mb-4">
              <ProductCard
                id={4}
                image="/assets/images/pro4.jpg"
                name="Áo mưa Thông Minh 1"
                price="499.000Đ"
              />
            </div>
            <div className="col-md-4 mb-4">
              <ProductCard
                id={5}
                image="/assets/images/pro5.jpg"
                name="Áo mưa Thông Minh 2"
                price="599.000Đ"
              />
            </div>
            <div className="col-md-4 mb-4">
              <ProductCard
                id={6}
                image="/assets/images/pro6.jpg"
                name="Áo mưa Thông Minh 3"
                price="699.000Đ"
              />
            </div>
          </div>
          {/* Nút xem thêm sản phẩm */}
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Áo Mưa Trẻ Em */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Trẻ Em</h2>
          <div className="row">
            <div className="col-md-4 mb-4">
              <ProductCard
                id={7}
                image="/assets/images/pro7.jpg"
                name="Áo mưa Trẻ Em 1"
                price="99.000Đ"
              />
            </div>
            <div className="col-md-4 mb-4">
              <ProductCard
                id={8}
                image="/assets/images/pro8.jpg"
                name="Áo mưa Trẻ Em 2"
                price="149.000Đ"
              />
            </div>
            <div className="col-md-4 mb-4">
              <ProductCard
                id={9}
                image="/assets/images/pro9.jpg"
                name="Áo mưa Trẻ Em 3"
                price="199.000Đ"
              />
            </div>
          </div>
          {/* Nút xem thêm sản phẩm */}
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
