import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import ProductModal
import '../styles/HomePage.css';

const HomePage = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const productsRando = [
    { id: 1, image: '/assets/images/pro1.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa tiện dụng và thời trang.' },
    { id: 2, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Chất liệu vải bền, giữ ấm tuyệt vời.' },
    { id: 3, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Dễ dàng mang theo và sử dụng.' },
  ];

  const productsThongMinh = [
    { id: 4, image: '/assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', description: 'Áo mưa cao cấp với thiết kế thông minh.' },
    { id: 5, image: '/assets/images/pro5.jpg', name: 'Áo mưa Thông Minh 2', price: '599.000Đ', description: 'Chất liệu chống thấm nước hiệu quả.' },
    { id: 6, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 3', price: '699.000Đ', description: 'Tính năng tiện lợi, dễ sử dụng.' },
  ];

  const productsTreEm = [
    { id: 7, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 1', price: '99.000Đ', description: 'Áo mưa dễ thương cho trẻ em.' },
    { id: 8, image: '/assets/images/pro8.jpg', name: 'Áo mưa Trẻ Em 2', price: '149.000Đ', description: 'Thiết kế đáng yêu, bảo vệ bé khỏi mưa.' },
    { id: 9, image: '/assets/images/pro9.jpg', name: 'Áo mưa Trẻ Em 3', price: '199.000Đ', description: 'Áo mưa cho trẻ em với màu sắc bắt mắt.' },
  ];

  // Mở modal với sản phẩm được chọn
  const openModal = (product) => {
    setSelectedProduct(product);
    setModalVisible(true);
  };

  // Đóng modal
  const closeModal = () => {
    setModalVisible(false);
    setSelectedProduct(null);
  };

  return (
    <div className="homepage">
      {/* Banner Section */}
      <section className="banner">
        <div id="bannerCarousel" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            {/* Slide 1 */}
            <div className="carousel-item active" style={{ backgroundImage: 'url(/assets/images/banner.jpg)' }}>
              <div className="carousel-caption">
                <h2>Áo mưa thời trang</h2>
                <p>Thoải mái và tiện dụng</p>
                <div className="buttons">
                  <button className="btn btn-warning me-2">Mua Ngay</button>
                  <button className="btn btn-outline-warning">Xem Thêm</button>
                </div>
              </div>
            </div>

            {/* Slide 2 */}
            <div className="carousel-item" style={{ backgroundImage: 'url(/assets/images/banner2.jpg)' }}>
              <div className="carousel-caption">
                <h2>Áo mưa bền bỉ</h2>
                <p>Chất liệu cao cấp, bảo vệ bạn mọi lúc mọi nơi</p>
                <div className="buttons">
                  <button className="btn btn-warning me-2">Mua Ngay</button>
                  <button className="btn btn-outline-warning">Xem Thêm</button>
                </div>
              </div>
            </div>
          </div>

          {/* Các nút điều khiển */}
          <button className="carousel-control-prev" type="button" data-bs-target="#bannerCarousel" data-bs-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button className="carousel-control-next" type="button" data-bs-target="#bannerCarousel" data-bs-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
      </section>

      {/* Các phần sản phẩm khác sẽ tiếp tục ở đây */}
    </div>
  );
};

export default HomePage;
