import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import hook useNavigate
import "../styles/LoginPage.css";  // ƒê·∫£m b·∫£o b·∫°n c√≥ t·ªáp CSS n√†y cho trang ƒëƒÉng nh·∫≠p

const LoginPage = () => {
  const [input, setInput] = useState(''); // L∆∞u tr·ªØ gi√° tr·ªã nh·∫≠p t·ª´ ng∆∞·ªùi d√πng (email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i)
  const [password, setPassword] = useState('');  // L∆∞u tr·ªØ m·∫≠t kh·∫©u
  const navigate = useNavigate();  // Hook ƒë·ªÉ chuy·ªÉn h∆∞·ªõng trang

  // H√†m x·ª≠ l√Ω khi ng∆∞·ªùi d√πng submit form
  const handleSubmit = (e) => {
    e.preventDefault();  // Ng·ª´ng h√†nh ƒë·ªông m·∫∑c ƒë·ªãnh c·ªßa form (reload trang)

    // C√°c bi·ªÉu th·ª©c ch√≠nh quy d√πng ƒë·ªÉ ki·ªÉm tra xem input c√≥ ph·∫£i l√† email hay s·ªë ƒëi·ªán tho·∫°i kh√¥ng
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;  // Gi·∫£ s·ª≠ s·ªë ƒëi·ªán tho·∫°i c√≥ t·ª´ 10 ƒë·∫øn 15 k√Ω t·ª±

    // Ki·ªÉm tra ƒë·∫ßu v√†o, n·∫øu l√† email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i h·ª£p l·ªá
    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
      alert('ƒêƒÉng nh·∫≠p th√†nh c√¥ng v·ªõi email!');
    } else if (phoneRegex.test(input)) {
      console.log(`S·ªë ƒëi·ªán tho·∫°i: ${input}, Password: ${password}`);
      alert('ƒêƒÉng nh·∫≠p th√†nh c√¥ng v·ªõi s·ªë ƒëi·ªán tho·∫°i!');
    } else {
      alert('Vui l√≤ng nh·∫≠p ƒë√∫ng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i!');
      return;  // N·∫øu ƒë·∫ßu v√†o kh√¥ng h·ª£p l·ªá, kh√¥ng chuy·ªÉn h∆∞·ªõng
    }

    // Chuy·ªÉn h∆∞·ªõng ng∆∞·ªùi d√πng v·ªÅ trang ch·ªß sau khi ƒëƒÉng nh·∫≠p th√†nh c√¥ng
    navigate('/');  // ƒê∆∞a ng∆∞·ªùi d√πng v·ªÅ trang HomePage
  };

  return (
    <div className="login-page">  {/* √Åp d·ª•ng l·ªõp CSS ƒë·ªÉ t·∫°o n·ªÅn cho trang ƒëƒÉng nh·∫≠p */}
      <div className="login-container">
