import React from 'react';
import { useCart } from '../contexts/CartContext'; // Import hook useCart để lấy giỏ hàng và các hàm thao tác
import { Link } from 'react-router-dom'; // Để tạo link đến các trang khác nếu cần

const Cart = () => {
  const { cartItems, removeFromCart, addToCart } = useCart(); // Lấy giỏ hàng, hàm thêm, xóa sản phẩm từ CartContext

  // Hàm xử lý xóa sản phẩm khỏi giỏ hàng
  const handleRemoveFromCart = (productId) => {
    removeFromCart(productId);
  };

  // Hàm xử lý thêm sản phẩm vào giỏ hàng
  const handleAddToCart = (product) => {
    addToCart(product);
  };

  if (cartItems.length === 0) {
    return (
      <div className="container">
        <h2>Your cart is empty.</h2>
        <Link to="/products">Go back to Products</Link>
      </div>
    );
  }

  return (
    <div className="container">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <img src={item.image} alt={item.name} width="100" />
            <div className="cart-item-details">
              <h4>{item.name}</h4>
              <p>Price: {item.price}</p>
              <p>Quantity: {item.quantity}</p>
              <button onClick={() => handleAddToCart(item)}>Add one more</button>
              <button onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-summary">
        <h4>Total: {cartItems.reduce((total, item) => total + item.price * item.quantity, 0)} VND</h4>
      </div>
    </div>
  );
};

export default Cart;
