import React, { useState, useEffect } from 'react';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal';
import { useCart } from '../contexts/CartContext';
import { useProduct } from '../contexts/ProductService'; // Import useProduct để lấy dữ liệu từ ProductProvider
import '../styles/ProductsPage.css';

const ProductsPage = () => {
  const { addToCart } = useCart();  // Hook để thêm sản phẩm vào giỏ
  const { products, categories, loading, error } = useProduct();  // Lấy sản phẩm và danh mục từ ProductContext

  // Lấy danh mục từ URL
  const category = new URLSearchParams(window.location.search).get('category'); 

  // Trạng thái cho modal chi tiết sản phẩm
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Hàm xử lý modal
  const handleProductClick = (product) => setSelectedProduct(product);
  const handleCloseModal = () => setSelectedProduct(null);

  // Hàm thêm vào giỏ hàng
  const handleAddToCart = (product) => {
    addToCart(product);
    alert(`${product.name} đã được thêm vào giỏ hàng!`);
  };

  // Render sản phẩm
  const renderProducts = (productList) =>
    productList.map((product) => (
      <div className="col-md-4 mb-4" key={product.id}>
        <ProductCard
          image={product.image}
          name={product.name}
          price={product.price}
          onClick={() => handleProductClick(product)} // Mở modal khi click vào sản phẩm
        />
        <button className="btn btn-primary mt-3" onClick={() => handleAddToCart(product)}>
          Thêm vào giỏ
        </button>
      </div>
    ));

  // Kiểm tra trạng thái loading và lỗi
  if (loading) {
    return <div>Đang tải...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">{category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}</h2>
      <div className="container">
        {category ? (
          <div className="row">
            {renderProducts(
              products.filter((product) => product.category === category) || [] // Lọc sản phẩm theo danh mục
            )}
          </div>
        ) : (
          Object.keys(categories).map((key) => (
            <div key={key}>
              <h3 className="text-center mb-5">{key.toUpperCase()}</h3>
              <div className="row">
                {renderProducts(categories[key] || [])}  {/* Hiển thị các sản phẩm theo danh mục */}
              </div>
            </div>
          ))
        )}
      </div>
      {selectedProduct && <ProductModal product={selectedProduct} onClose={handleCloseModal} />}
    </div>
  );
};

export default ProductsPage;
