import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Import hook useNavigate
import "../styles/RegisterPage.css";  // CSS ri√™ng cho trang ƒëƒÉng k√Ω

const RegisterPage = () => {
  const [input, setInput] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;

    if (emailRegex.test(input)) {
      console.log(`Email: ${input}, Password: ${password}`);
    } else if (phoneRegex.test(input)) {
      console.log(`S·ªë ƒëi·ªán tho·∫°i: ${input}, Password: ${password}`);
    } else {
      alert('Vui l√≤ng nh·∫≠p ƒë√∫ng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i!');
      return;
    }

    if (password !== confirmPassword) {
      alert('M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp!');
      return;
    }

    alert('ƒêƒÉng k√Ω th√†nh c√¥ng!');
    navigate('/login');  // Chuy·ªÉn h∆∞·ªõng v·ªÅ trang ƒëƒÉng nh·∫≠p
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-box">
          <h2>REGISTER FORM</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                required
              />
              <span className="icon">@</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <span className="icon">üîí</span>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="CONFIRM PASSWORD"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <span className="icon">üîí</span>
            </div>
            <button type="submit" className="register-btn">REGISTER</button>
          </form>
          <div className="login-link">
            <p>Already have an account? ‚Üí <a href="/login">Login Here</a></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
