import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../api/userAPI'; // Import h√†m register t·ª´ userAPI
import "../styles/RegisterPage.css"; // Import CSS

const RegisterPage = () => {
  const [input, setInput] = useState(''); // Tr∆∞·ªùng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i
  const [password, setPassword] = useState(''); // Tr∆∞·ªùng m·∫≠t kh·∫©u
  const [confirmPassword, setConfirmPassword] = useState(''); // X√°c nh·∫≠n m·∫≠t kh·∫©u
  const [errorMessage, setErrorMessage] = useState(''); // Th√¥ng b√°o l·ªói
  const navigate = useNavigate(); // ƒêi·ªÅu h∆∞·ªõng trang

  const handleSubmit = async (e) => {
    e.preventDefault(); // NgƒÉn ch·∫∑n h√†nh vi m·∫∑c ƒë·ªãnh c·ªßa form

    // Ki·ªÉm tra t√≠nh h·ª£p l·ªá c·ªßa email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;
    if (!emailRegex.test(input) && !phoneRegex.test(input)) {
      alert('Vui l√≤ng nh·∫≠p ƒë√∫ng email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i!');
      return;
    }

    // Ki·ªÉm tra m·∫≠t kh·∫©u x√°c nh·∫≠n c√≥ kh·ªõp kh√¥ng
    if (password !== confirmPassword) {
      alert('M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp!');
      return;
    }

    try {
      const response = await register(input, password); // G·ªçi API ƒëƒÉng k√Ω

      if (response.success) {
        alert('ƒêƒÉng k√Ω th√†nh c√¥ng!');
        navigate('/login'); // Chuy·ªÉn h∆∞·ªõng v·ªÅ trang ƒëƒÉng nh·∫≠p
      }
    } catch (error) {
      console.error('ƒêƒÉng k√Ω th·∫•t b·∫°i:', error);
      setErrorMessage('C√≥ l·ªói x·∫£y ra, vui l√≤ng th·ª≠ l·∫°i!');
    }
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-box">
          <h2>REGISTER FORM</h2>
          <form onSubmit={handleSubmit}>
            {/* Tr∆∞·ªùng nh·∫≠p email ho·∫∑c s·ªë ƒëi·ªán tho·∫°i */}
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)} // C·∫≠p nh·∫≠t gi√° tr·ªã input
                required
              />
              <span className="icon">@</span>
            </div>
            {/* Tr∆∞·ªùng nh·∫≠p m·∫≠t kh·∫©u */}
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)} // C·∫≠p nh·∫≠t m·∫≠t kh·∫©u
                required
              />
              <span className="icon">üîí</span>
            </div>
            {/* Tr∆∞·ªùng nh·∫≠p x√°c nh·∫≠n m·∫≠t kh·∫©u */}
            <div className="input-group">
              <input
                type="password"
                placeholder="CONFIRM PASSWORD"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)} // C·∫≠p nh·∫≠t x√°c nh·∫≠n m·∫≠t kh·∫©u
                required
              />
              <span className="icon">üîí</span>
            </div>
            {/* Hi·ªÉn th·ªã l·ªói n·∫øu c√≥ */}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {/* N√∫t ƒëƒÉng k√Ω */}
            <button type="submit" className="register-btn">REGISTER</button>
          </form>
          {/* Li√™n k·∫øt ƒë·∫øn trang ƒëƒÉng nh·∫≠p */}
          <div className="login-link">
            <p>
              Already have an account? ‚Üí <a href="/login">Login Here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
