import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../api/userAPI'; // Import hàm register từ userAPI
import "../styles/RegisterPage.css"; // Import CSS

const RegisterPage = () => {
  const [input, setInput] = useState(''); // Trường email hoặc số điện thoại
  const [password, setPassword] = useState(''); // Trường mật khẩu
  const [confirmPassword, setConfirmPassword] = useState(''); // Xác nhận mật khẩu
  const [errorMessage, setErrorMessage] = useState(''); // Thông báo lỗi
  const navigate = useNavigate(); // Điều hướng trang

  const handleSubmit = async (e) => {
    e.preventDefault(); // Ngăn chặn hành vi mặc định của form

    // Kiểm tra tính hợp lệ của email hoặc số điện thoại
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,15}$/;
    if (!emailRegex.test(input) && !phoneRegex.test(input)) {
      alert('Vui lòng nhập đúng email hoặc số điện thoại!');
      return;
    }

    // Kiểm tra mật khẩu xác nhận có khớp không
    if (password !== confirmPassword) {
      alert('Mật khẩu xác nhận không khớp!');
      return;
    }

    try {
      const response = await register(input, password); // Gọi API đăng ký

      if (response.success) {
        alert('Đăng ký thành công!');
        navigate('/login'); // Chuyển hướng về trang đăng nhập
      }
    } catch (error) {
      console.error('Đăng ký thất bại:', error);
      setErrorMessage('Có lỗi xảy ra, vui lòng thử lại!');
    }
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-box">
          <h2>REGISTER FORM</h2>
          <form onSubmit={handleSubmit}>
            {/* Trường nhập email hoặc số điện thoại */}
            <div className="input-group">
              <input
                type="text"
                placeholder="EMAIL or PHONE"
                value={input}
                onChange={(e) => setInput(e.target.value)} // Cập nhật giá trị input
                required
              />
              <span className="icon">@</span>
            </div>
            {/* Trường nhập mật khẩu */}
            <div className="input-group">
              <input
                type="password"
                placeholder="PASSWORD"
                value={password}
                onChange={(e) => setPassword(e.target.value)} // Cập nhật mật khẩu
                required
              />
              <span className="icon">🔒</span>
            </div>
            {/* Trường nhập xác nhận mật khẩu */}
            <div className="input-group">
              <input
                type="password"
                placeholder="CONFIRM PASSWORD"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)} // Cập nhật xác nhận mật khẩu
                required
              />
              <span className="icon">🔒</span>
            </div>
            {/* Hiển thị lỗi nếu có */}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {/* Nút đăng ký */}
            <button type="submit" className="register-btn">REGISTER</button>
          </form>
          {/* Liên kết đến trang đăng nhập */}
          <div className="login-link">
            <p>
              Already have an account? → <a href="/login">Login Here</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
