import React from 'react';
import { useCart } from '../contexts/CartContext';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2'; // SweetAlert2
import 'bootstrap/dist/css/bootstrap.min.css';
import "../styles/Checkout.css"; // File CSS tùy chỉnh

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const navigate = useNavigate();

  // Tính tổng giá trị giỏ hàng
  const totalPrice = cartItems.reduce(
    (total, item) =>
      total + parseFloat(item.price.replace('Đ', '').replace('.', '').trim()) * item.quantity,
    0
  );

  // Hàm xử lý khi thanh toán
  const handleCheckout = () => {
    // Xóa giỏ hàng khi thanh toán thành công
    clearCart();

    // Hiển thị hộp thoại thanh toán thành công với nút quay về trang chủ
    Swal.fire({
      icon: 'success',
      title: '<strong>Thanh toán thành công!</strong>',
      html: '<p>Cảm ơn bạn đã mua hàng!</p>',
      confirmButtonText: '<i class="fas fa-home"></i> Quay về trang chủ',
      iconColor: '#28a745', // Màu xanh lá
      background: '#f8f9fa', // Màu nền sáng
      confirmButtonColor: '#007bff', // Màu nút xanh Bootstrap
      customClass: {
        confirmButton: 'btn btn-primary btn-lg rounded-pill px-4 py-2', // Giao diện nút
      },
      buttonsStyling: false, // Tắt kiểu mặc định của SweetAlert2
    }).then(() => {
      // Chuyển hướng về trang chủ sau khi đóng thông báo
      navigate('/');
    });
  };

  return (
    <div className="checkout-page container py-5">
      <h2 className="text-center mb-4">Thanh Toán</h2>
      <div className="row">
        {/* Cột bên trái: Thông tin giỏ hàng */}
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title mb-4">Thông tin giỏ hàng</h5>
              {cartItems.length > 0 ? (
                <ul className="list-group">
                  {cartItems.map((item) => (
                    <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>{item.name}</strong> x {item.quantity}
                      </div>
                      <span>{item.price}</span>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>Giỏ hàng của bạn đang trống.</p>
              )}
            </div>
          </div>
        </div>

        {/* Cột bên phải: Thông tin người nhận và tổng tiền */}
        <div className="col-md-4">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title mb-4">Thông tin người nhận</h5>
              <form>
                <div className="mb-3">
                  <label htmlFor="name" className="form-label">Họ và tên</label>
                  <input type="text" className="form-control" id="name" required />
                </div>
                <div className="mb-3">
                  <label htmlFor="address" className="form-label">Địa chỉ giao hàng</label>
                  <input type="text" className="form-control" id="address" required />
                </div>
                <div className="mb-3">
                  <label htmlFor="phone" className="form-label">Số điện thoại</label>
                  <input type="tel" className="form-control" id="phone" required />
                </div>
              </form>
            </div>
          </div>
          <div className="card">
            <div className="card-body">
              <h5 className="card-title mb-4">Tổng tiền</h5>
              <p className="total-price text-danger fs-4">
                {totalPrice.toLocaleString()} Đ
              </p>
              <button className="btn btn-success btn-lg w-100" onClick={handleCheckout}>
                Xác nhận thanh toán
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
