import React from 'react';
import PropTypes from 'prop-types';
import '../styles/ProductModal.css';

const ProductModal = ({ product, onClose }) => {
  if (!product) return null; // Nếu không có sản phẩm thì không hiển thị modal

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>X</button>
        <img src={product.image} alt={product.name} className="modal-image" />
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p><strong>{product.price}</strong></p>
        <button className="btn btn-warning">Mua Ngay</button>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.object, // Sản phẩm chi tiết
  onClose: PropTypes.func.isRequired, // Hàm đóng modal
};

export default ProductModal;
