import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import ProductModal from '../components/ProductModal'; // Import ProductModal
import '../styles/HomePage.css';

const HomePage = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    { id: 1, image: '/assets/images/pro1.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ', description: 'Áo mưa tiện dụng và thời trang.' },
    { id: 2, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ', description: 'Chất liệu vải bền, giữ ấm tuyệt vời.' },
    { id: 3, image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ', description: 'Dễ dàng mang theo và sử dụng.' },
  ];

  const smartProducts = [
    { id: 4, image: '/assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ', description: 'Mô tả sản phẩm 1' },
    { id: 5, image: '/assets/images/pro5.jpg', name: 'Áo mưa Thông Minh 2', price: '599.000Đ', description: 'Mô tả sản phẩm 2' },
    { id: 6, image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 3', price: '699.000Đ', description: 'Mô tả sản phẩm 3' },
  ];

  const kidsProducts = [
    { id: 7, image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 1', price: '99.000Đ', description: 'Mô tả sản phẩm trẻ em 1' },
    { id: 8, image: '/assets/images/pro8.jpg', name: 'Áo mưa Trẻ Em 2', price: '149.000Đ', description: 'Mô tả sản phẩm trẻ em 2' },
    { id: 9, image: '/assets/images/pro9.jpg', name: 'Áo mưa Trẻ Em 3', price: '199.000Đ', description: 'Mô tả sản phẩm trẻ em 3' },
  ];

  // Mở modal với sản phẩm được chọn
  const openModal = (product) => {
    setSelectedProduct(product);
    setModalVisible(true);
  };

  // Đóng modal
  const closeModal = () => {
    setModalVisible(false);
    setSelectedProduct(null);
  };

  return (
    <div className="homepage">
      {/* Banner Section */}
      <section className="banner">
        <div id="bannerCarousel" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            <div className="carousel-item active" style={{ backgroundImage: 'url(/assets/images/banner1.jpg)' }}>
              <div className="carousel-caption">
                <h2>Áo mưa thời trang</h2>
                <p>Thoải mái và tiện dụng</p>
                <div className="buttons">
                  <button className="btn btn-warning me-2">Mua Ngay</button>
                  <button className="btn btn-outline-warning">Xem Thêm</button>
                </div>
              </div>
            </div>
            <div className="carousel-item" style={{ backgroundImage: 'url(/assets/images/banner2.jpg)' }}>
              <div className="carousel-caption">
                <h2>Áo mưa bền bỉ</h2>
                <p>Chất liệu cao cấp, bảo vệ bạn mọi lúc mọi nơi</p>
                <div className="buttons">
                  <button className="btn btn-warning me-2">Mua Ngay</button>
                  <button className="btn btn-outline-warning">Xem Thêm</button>
                </div>
              </div>
            </div>
          </div>
          <button className="carousel-control-prev" type="button" data-bs-target="#bannerCarousel" data-bs-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button className="carousel-control-next" type="button" data-bs-target="#bannerCarousel" data-bs-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
      </section>

      {/* Áo Mưa Rando */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Rando</h2>
          <div className="row">
            {products.map((product) => (
              <div className="col-md-4 mb-4" key={product.id} onClick={() => openModal(product)}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                />
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Áo Mưa Thông Minh */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Thông Minh</h2>
          <div className="row">
            {smartProducts.map((product) => (
              <div className="col-md-4 mb-4" key={product.id} onClick={() => openModal(product)}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                />
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Áo Mưa Trẻ Em */}
      <section className="product-list">
        <div className="container">
          <h2 className="text-center mb-5">Áo Mưa Trẻ Em</h2>
          <div className="row">
            {kidsProducts.map((product) => (
              <div className="col-md-4 mb-4" key={product.id} onClick={() => openModal(product)}>
                <ProductCard
                  id={product.id}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                />
              </div>
            ))}
          </div>
          <div className="text-center">
            <Link to="/products" className="btn btn-warning">Xem Thêm Sản Phẩm</Link>
          </div>
        </div>
      </section>

      {/* Hiển thị Modal nếu có sản phẩm được chọn */}
      {modalVisible && selectedProduct && (
        <ProductModal product={selectedProduct} onClose={closeModal} />
      )}
    </div>
  );
};

export default HomePage;
