import React from 'react';
import ProductCard from '../components/ProductCard';

const ProductsPage = () => {
  // Dữ liệu các sản phẩm theo các danh mục
  const products = {
    NhãnHiệuHiếuĐức: [
      { image: '/assets/images/pro2.jpg', name: 'Áo mưa Rando 1', price: '199.000Đ' },
      { image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 2', price: '299.000Đ' },
      { image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 3', price: '399.000Đ' },
      { image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 4', price: '399.000Đ' },
      { image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 5', price: '399.000Đ' },
      { image: '/assets/images/pro3.jpg', name: 'Áo mưa Rando 6', price: '399.000Đ' },
    ],
    ÁoMưaThôngMinh: [
      { image: '/assets/images/pro4.jpg', name: 'Áo mưa Thông Minh 1', price: '499.000Đ' },
      { image: '/assets/images/pro5.jpg', name: 'Áo mưa Thông Minh 2', price: '599.000Đ' },
      { image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 3', price: '699.000Đ' },
      { image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 4', price: '699.000Đ' },
      { image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 5', price: '699.000Đ' },
      { image: '/assets/images/pro6.jpg', name: 'Áo mưa Thông Minh 6', price: '699.000Đ' },
    ],
    ÁoMưaTrẻEm: [
      { image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 1', price: '99.000Đ' },
      { image: '/assets/images/pro8.jpg', name: 'Áo mưa Trẻ Em 2', price: '149.000Đ' },
      { image: '/assets/images/pro9.jpg', name: 'Áo mưa Trẻ Em 3', price: '199.000Đ' },
      { image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 4', price: '99.000Đ' },
      { image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 5', price: '99.000Đ' },
      { image: '/assets/images/pro7.jpg', name: 'Áo mưa Trẻ Em 6', price: '99.000Đ' },
    ],
  };

  // Lấy danh mục từ URL (sử dụng useLocation để lấy tham số category nếu có)
  const category = new URLSearchParams(window.location.search).get('category');

  return (
    <div className="products-page">
      <h2 className="text-center mb-5">{category ? category.toUpperCase() : 'Tất Cả Sản Phẩm'}</h2>

      {/* Hiển thị danh mục sản phẩm theo category */}
      <div className="container">
        {category ? (
          // Hiển thị theo danh mục được chọn
          <div className="row">
            {products[category]?.map((product, index) => (
              <div className="col-md-4 mb-4" key={index}>
                <ProductCard
                  image={product.image}
                  name={product.name}
                  price={product.price}
                />
              </div>
            ))}
          </div>
        ) : (
          // Hiển thị tất cả các sản phẩm
          Object.keys(products).map((categoryKey) => (
            <div key={categoryKey}>
              <h3 className="text-center mb-5">{categoryKey.toUpperCase()}</h3>
              <div className="row">
                {products[categoryKey].map((product, index) => (
                  <div className="col-md-4 mb-4" key={index}>
                    <ProductCard
                      image={product.image}
                      name={product.name}
                      price={product.price}
                    />
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ProductsPage;
