import React from 'react';
import PropTypes from 'prop-types';
import '../styles/ProductCard.css';

const ProductCard = ({ id, image, name, price, onClick, onAddToCart }) => {
  return (
    <div className="card">
      <img src={image} alt={name} className="card-img-top" />
      <div className="card-body text-center">
        <h5 className="card-title">{name}</h5>
        <p className="card-text">{price}</p>
      </div>

      {/* Nút xem chi tiết */}
      <div className="card-footer text-center">
        <button className="btn btn-info" onClick={() => onClick(id)}>
          Xem chi tiết
        </button>

        {/* Nút thêm vào giỏ */}
        <button className="btn btn-primary" onClick={() => onAddToCart(id)}>
          Thêm vào giỏ
        </button>
      </div>
    </div>
  );
};

ProductCard.propTypes = {
  id: PropTypes.number.isRequired, // ID của sản phẩm
  image: PropTypes.string.isRequired, // Hình ảnh sản phẩm
  name: PropTypes.string.isRequired, // Tên sản phẩm
  price: PropTypes.string.isRequired, // Giá sản phẩm
  onClick: PropTypes.func.isRequired, // Hàm xử lý click vào xem chi tiết
  onAddToCart: PropTypes.func.isRequired, // Hàm xử lý thêm vào giỏ
};

export default ProductCard;
