import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '../styles/ProductModal.css'; // Đường dẫn CSS modal

const ProductModal = ({ product, onClose }) => {
  // Di chuyển các hooks ra ngoài điều kiện
  const [selectedSize, setSelectedSize] = useState(null);  // Quản lý size đã chọn
  const [selectedColor, setSelectedColor] = useState('');  // Quản lý màu sắc đã chọn

  // Kiểm tra xem có sản phẩm hay không trước khi render nội dung modal
  if (!product) return null; // Không hiển thị nếu không có sản phẩm

  const handleBuyNow = () => {
    alert(`Bạn đã chọn mua ngay sản phẩm: ${product.name}`);
    // Có thể thêm logic chuyển hướng đến trang thanh toán tại đây
  };

  const handleSizeClick = (size) => {
    setSelectedSize(size); // Cập nhật size đã chọn
  };

  const handleColorClick = (color) => {
    setSelectedColor(color); // Cập nhật màu sắc đã chọn
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Nút đóng */}
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i> {/* Icon đóng */}
        </button>

        {/* Nội dung chi tiết sản phẩm */}
        <div className="modal-body" style={{ display: 'flex', alignItems: 'flex-start', gap: '20px' }}>
          {/* Hình ảnh sản phẩm */}
          <div className="modal-image" style={{ flex: '1' }}>
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: '100%',
                height: 'auto',
                objectFit: 'contain',
                borderRadius: '8px',
              }}
            />
          </div>

          {/* Chi tiết sản phẩm */}
          <div className="modal-details" style={{ flex: '2' }}>
            <h2 style={{ marginBottom: '15px' }}>{product.name}</h2>
            <p style={{ fontSize: '14px', color: '#666', marginBottom: '20px' }}>{product.description}</p>
            <p style={{ fontWeight: 'bold', fontSize: '18px', color: '#ff5722' }}>
              Giá hiện tại: {product.price}
            </p>

            {/* Tùy chọn size */}
            <div style={{ margin: '20px 0' }}>
              <strong>Size:</strong>
              <div className="size-options">
                {['S', 'M', 'L', 'XL'].map((size) => (
                  <div
                    key={size}
                    className={`size-option ${selectedSize === size ? 'selected' : ''}`}
                    onClick={() => handleSizeClick(size)}
                  >
                    {size}
                  </div>
                ))}
              </div>
            </div>

            {/* Tùy chọn màu sắc */}
            <div style={{ marginTop: '10px' }}>
              <strong>Màu sắc:</strong>
              <span style={{ marginLeft: '10px', display: 'flex', gap: '10px' }}>
                <span
                  className="orange"
                  onClick={() => handleColorClick('orange')}
                  style={{
                    backgroundColor: selectedColor === 'orange' ? 'orange' : '',
                  }}
                />
                <span
                  className="blue"
                  onClick={() => handleColorClick('blue')}
                  style={{
                    backgroundColor: selectedColor === 'blue' ? 'blue' : '',
                  }}
                />
                <span
                  className="green"
                  onClick={() => handleColorClick('green')}
                  style={{
                    backgroundColor: selectedColor === 'green' ? 'green' : '',
                  }}
                />
              </span>
            </div>

            {/* Nhập số lượng */}
            <div style={{ marginBottom: '20px' }}>
              <label htmlFor="quantity" style={{ fontWeight: 'bold' }}>Số lượng đặt mua:</label>
              <input
                type="number"
                id="quantity"
                min="1"
                defaultValue="1"
                style={{
                  marginLeft: '10px',
                  padding: '5px',
                  width: '60px',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                }}
              />
            </div>

            {/* Nút Mua ngay */}
            <button
              className="btn btn-warning"
              style={{
                padding: '10px 20px',
                fontSize: '16px',
                backgroundColor: '#ff9800',
                border: 'none',
                color: '#fff',
                borderRadius: '4px',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
              }}
              onClick={handleBuyNow}
            >
              <i className="fas fa-shopping-cart" style={{ marginRight: '8px' }}></i> {/* Icon giỏ hàng */}
              Mua Ngay
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
  }).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ProductModal;
