import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '../styles/ProductModal.css'; // Đường dẫn CSS modal

const ProductModal = ({ product, onClose }) => {
  // Đảm bảo các hook luôn được gọi ở đầu component
  const [size, setSize] = useState('S');
  const [color, setColor] = useState('orange');
  const [quantity, setQuantity] = useState(1);

  if (!product) {
    return null; // Không hiển thị modal nếu không có sản phẩm
  }

  const handleBuyNow = () => {
    alert(`Bạn đã chọn mua ngay sản phẩm: ${product.name} - Size: ${size}, Màu: ${color}, Số lượng: ${quantity}`);
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Nút đóng */}
        <button className="modal-close" onClick={onClose}>
          &times;
        </button>

        {/* Nội dung chi tiết sản phẩm */}
        <div className="modal-body" style={{ display: 'flex', alignItems: 'flex-start', gap: '20px' }}>
          {/* Hình ảnh sản phẩm */}
          <div className="modal-image">
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: '100%',
                height: 'auto',
                objectFit: 'contain',
                borderRadius: '8px',
              }}
            />
          </div>

          {/* Chi tiết sản phẩm */}
          <div className="modal-details">
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p>Giá: {product.price}</p>

            {/* Tùy chọn Size */}
            <div>
              <strong>Size:</strong>
              <div>
                {['S', 'M', 'L', 'XL'].map((s) => (
                  <button
                    key={s}
                    style={{
                      margin: '5px',
                      padding: '5px 10px',
                      backgroundColor: size === s ? '#ff9800' : '#e0e0e0',
                      color: size === s ? '#fff' : '#000',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                    }}
                    onClick={() => setSize(s)}
                  >
                    {s}
                  </button>
                ))}
              </div>
            </div>

            {/* Tùy chọn Màu sắc */}
            <div>
              <strong>Màu sắc:</strong>
              <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                {['orange', 'blue', 'green'].map((c) => (
                  <span
                    key={c}
                    style={{
                      width: '30px',
                      height: '30px',
                      backgroundColor: c,
                      border: color === c ? '3px solid #000' : '1px solid #ccc',
                      borderRadius: '50%',
                      cursor: 'pointer',
                    }}
                    onClick={() => setColor(c)}
                  ></span>
                ))}
              </div>
            </div>

            {/* Số lượng */}
            <div style={{ marginTop: '15px' }}>
              <label>
                <strong>Số lượng:</strong>
              </label>
              <input
                type="number"
                value={quantity}
                min="1"
                onChange={(e) => setQuantity(parseInt(e.target.value, 10) || 1)}
                style={{
                  marginLeft: '10px',
                  width: '50px',
                  padding: '5px',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                }}
              />
            </div>

            {/* Nút Mua ngay */}
            <div style={{ marginTop: '20px' }}>
              <button
                style={{
                  padding: '10px 20px',
                  fontSize: '16px',
                  backgroundColor: '#ff9800',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
                onClick={handleBuyNow}
              >
                Mua Ngay
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

ProductModal.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
  }).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ProductModal;
